import React from "react";
import axios from "axios";
import "./css/LoginStyle.css"
import { Link } from "react-router-dom";



export default function LoginPage(){

	const [state , setState] = React.useState({
		"email": "" ,
		"password":""
	})

	const SaveData= (e) => { 
        const {name , value }= e.target
        
        setState((old) => { 
            return {...old , [name]:value}
         })
     }

	const login =(e) => {
		e.preventDefault()
		let data= new FormData()
		data.append("method" , "register")
		data.append("email" , state.email)
		data.append("password" , state.password)

axios.post(`http://localhost:8000/api/login/`, data)
    .then((res) => { 
        console.log(res) 
        localStorage.setItem(res.data.access_token)


     })
    
    .catch((error) => console.log(error))
	
    // window.location.href="http://localhost:3000/profile"
	}
	

    return(
      <div>
          
              <div className="form-container">
        
        
                  <form className="login-form login-right" onSubmit={login}>
                      <h2>Login</h2>
                      <label>Email :</label>
                      <input className="inp" type='email' name='email' placeholder="Enter email" onChange={e=>SaveData(e)}/>
                      
                      <label>Password :</label>
                      <input className="inp" type='password' placeholder="Enter password" name='password'onChange={e=>SaveData(e)}/>
        
                      <input className="btn" type='submit' value="submit"/>
                      <p>Don't have an account ? <Link>Register</Link>  </p>
        
                  </form>
        
                  <div className="login-left">
                      
                      <img src={require("./images/venom.png")} alt="logo" style={{width:"200px", height:"200px" , borderRadius:"50px"}} />
                         <p className="fit"   >    Fitness  </p> 
                  </div>
        
        
              </div>
              <Link to="/home">Back to home</Link>

      </div>
    )
}
