import React, { useState } from 'react'
import axios from 'axios'



export default function Register() {
  
const [nomCl,setNomCl]=useState("");
const [email,setEmail]=useState("");
const [password,setPasword]=useState("");

    function register(e){
    e.preventDefault();
    var data=new FormData();
   
    data.append('method','register')
    data.append("nomCl",nomCl)
    data.append("email",email)
    data.append("password",password)
    axios.post('http://localhost:8000/api/register',data)
    .then(function(response){
      console.log(response.data);
      if(response.data.message){
        console.log("send")
       // window.location.href = "/";
       window.location.replace('http://localhost:3000')
      }

    }).catch(error => {
      console.log(error);
    });
    
    
  }
  return (
    <div>
      <form onSubmit={register}>
        <input type='text' name='nomCl' onChange={e=>setNomCl(e.target.value)} required />
        <input type='text' name='email' onChange={e=>setEmail(e.target.value)}/>
        <input type='text' name='password'onChange={e=>setPasword(e.target.value)}/>
        <input type='submit' value={"goooo"}/>
      </form>
      
    </div>
  )
}
